    var keys = tableData[0],
        objects = [];

    for(var i = 1; i < tableData.length; i++){
        var rowObject = {};
        tableData[i].forEach(function(item, index){
            rowObject[keys[index]] = item;
        });
        objects.push(rowObject);
    }

    //return JSON.stringify(objects);
    return objects;
}

